import React, { useState } from 'react';
import "./ExpenseItem.css";
import ExpenseDate from "./ExpenseDate";
import Card from "../UI/Card";

const ExpenseItem = (props) => {
 
  //ola ta react hooks ksekinane me to use

  const [title, setTitle] = useState(props.title);
  //array destructuring 
  // epistrefei enna array pou i prwti timi
  //einai h timi tou props.title
  //kai h deuteri timi einai ena function setTitle

  
  
  const clickHandler = () => {
    setTitle("Updated!");
    //otan kaloume to setTitle
    //diaxirizete thn allagi h react
    //kai to component kallite ksana otan allazei to state
  }

  return (
    <Card className="expense-item">
      <ExpenseDate date={props.date} />
      <div className="expense-item__description">
        <h2>{props.title}</h2>
      </div>
      <div className="expense-item__price">${props.amount}</div>
      <button onClick={clickHandler} >Change Title</button>
    </Card> 
  );
}

export default ExpenseItem;
